import {
  Box,
  Heading,
  SimpleGrid,
  Text,
  toast,
  useToast,
} from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import { CoursesGroup } from "./courseGroup";
import { MenuNav } from "./menuNav";
import { Plate } from "./plate";
import { ShoppingCartLine } from "./shoppingCart";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const toast = useToast();

  const addToShoppingCart = (plate, amount, courseId) => {
    setShoppingCart(
      shoppingCart.concat({
        plate,
        amount,
        extra: null,
        courseId,
      })
    );
    toast({
      title: "Plato añadido!",
      
      status: "success",
      duration: 9000,
      isClosable: true,
      position:"top",
      render: () => (
        <Box bgColor={"#a0e041"} m="3" p="3" rounded="lg" shadow="lg" >
          <Text size="md">{`Se ${
        amount > 1
          ? `han añadido al pedido ${amount}`
          : "ha añadido al pedido un"
      } ${plate.name} `}</Text>
          <CoursesGroup   amount={amount} plate={plate} courseId={courseId} />
        </Box>
      ),
    });
    setSelectedItem(null);
  };

  const remove = (course) => {
    setShoppingCart(shoppingCart.filter((aCourse) => aCourse != course));
  };

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];
  const categories = categoryNames
    .map((name) => {
      return {
        name,
        items: menuItems.filter((item) => item.category === name),
      };
    })
    .filter((cat) => cat.name);

  const Category = (props) => {
    const { name, items } = props.category;
    return (
      <Box m="5">
        <Box m="5">
          <Heading as="h3">{name}</Heading>
        </Box>
        <SimpleGrid columns={[1, 2, 3, 4]} spacing="40px">
          {items.map((item) => (
            <Plate item={item} addToShoppingCart={addToShoppingCart} />
          ))}
        </SimpleGrid>
      </Box>
    );
  };

  const Categories = () => categories.map((cat) => <Category category={cat} />);

  return (
    <>
      <MenuNav shoppingCart={shoppingCart} remove={remove} />
      <Box pt="20" bg="#ebebeb">
        <Categories />
      </Box>
    </>
  );
};
