import {
  Box,
  Button,
  Container,
  Heading,
  SimpleGrid,
  Switch,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import { getAllMenuItems } from "../../scripts/GetAllMenuItems";
import { MenuItemIcon } from "../menu/menuItemIcon";
import { MenuNav } from "../menu/menuNav";
import { DashboardNav } from "./dashboardNav";
import { MenuItemEdit } from "./menuItemEdit";
import { MenuItemEditIcon } from "./menuItemEditIcon";
import { NewMenuItem } from "./editMenuItemWindow";

export const Dashboard = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => getAllMenuItems().then((res) => setMenuItems(res)), []);

  const emptyMenuItem = {
    name: "",
    category: "",
    description: "",
    imgURL: "",
    variants: [],
    tags: [],
    extras: [],
    aditionalOptions: [],
  }

  return (
    <>
      <DashboardNav />

      <Box m="10">
        <SimpleGrid spacing={3} columns={[1, 2, 3, 4]}>
          <Button h="70%">
            Nuevo Plato o Producto
          </Button>
          <MenuItemEdit item={emptyMenuItem} />
          {menuItems.map((item) => (
            <MenuItemEdit item={item} />
          ))}
        </SimpleGrid>
      </Box>
    </>
  );
};
