import {
  Box,
  Button,
  CloseButton,
  Heading,
  HStack,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  Wrap,
  Text,
  Input,
  WrapItem,
  Image,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useState } from "react";

export const PlateWindow = (props) => {
  const { _id, imgURL, name, description, price } = props.item;

  const [amount, setAmount] = useState(1);

  const increment = () => setAmount((amount) => amount + 1);

  const decrement = () =>
    setAmount((amount) => {
      if (amount > 1) return amount - 1;
      else return amount;
    });

  const LastPlateIcon = () => (
      <Image
				as={motion.img}
        borderRadius="full"
        boxSize="40px"
        src={imgURL}
        alt={name}
        fit="cover"
        shadow="md"
        display="inline"
        initial={{ y: -50 }}
        animate={{ y: 0 }}
				margin="-30"
      />
  );

  const PlateIcon = () => (

    <Image
			as={motion.img}
      borderRadius="full"
      boxSize="40px"
      src={imgURL}
      alt={name}
      fit="cover"
      shadow="md"
      display="inline"
      marginLeft="-30"

    />
  );

  return (
    <>
      <Box
        as={motion.div}
        position="fixed"
        bg="rgba(255,255,255,0.5)"
        w="100%"
        h="100%"
        top="0"
        left="0"
        onClick={() => props.setSelectedItem(null)}
        transition={{ duration: 2 }}
      />
      <Box
        as={motion.div}
        position="fixed"
        top={0}
        boxShadow="dark-lg"
        rounded="xl"
        padding="3"
        bg="white"
        animate={{ opacity: 1 }}
        initial={{ opacity: 0 }}
      >
        <Box>
          <CloseButton onClick={() => props.setSelectedItem(null)} />
          <Wrap spacing={10} margin="2">
            <Box>
              <WrapItem as={motion.div} layoutId={_id}>
                <Image as={motion.img} src={imgURL} rounded="xl" />
              </WrapItem>
            </Box>
            <Box margin="4">
              <Box as={motion.div} layoutId={name}>
                <Heading size="lg">{name}</Heading>
              </Box>
              <Text marginTop={10}>{description}</Text>
              <Box as={motion.div}>
                <Heading size="md">${price}</Heading>
              </Box>
              <HStack marginTop={20}>
                <NumberInput size="lg" maxW={32} value={amount}>
                  <NumberInputField />
                  <NumberInputStepper>
                    <NumberIncrementStepper
                      onClick={() => setAmount((amount) => amount + 1)}
                    />
                    <NumberDecrementStepper onClick={decrement} />
                  </NumberInputStepper>
                </NumberInput>
                <Input placeholder="Pedido especial?" size="lg" />
                <Button
                  colorScheme="blue"
                  size="lg"
                  onClick={() => props.addToShoppingCart(amount)}
                >
                  Pedir
                </Button>
              </HStack>
              <Box borderWidth="1px" borderRadius="lg" margin="5" padding="2">
								<Box  marginLeft="30">

                {Array(amount - 1).fill(<PlateIcon layoutId={`${name}${Math.random()}`}  />)}
                <LastPlateIcon />
								</Box>
              </Box>
            </Box>
          </Wrap>
        </Box>
      </Box>
    </>
  );
};
