import {
  Text,
  CloseButton,
 
  Stat,
  StatNumber,
  StatHelpText,
  SimpleGrid,
  Badge,
  Box,
  Button,
  Heading,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect } from "react";
import { CoursesGroup } from "./courseGroup";



export const ShoppingCart = (props) => {
  const { isOpen, onOpen, onClose } = useDisclosure();


  const total = () => {
    let total = 0;
    props.shoppingCart
      .map((course) => course.amount * course.plate.price)
      .forEach((x) => (total += x));
    return total;
  };



  return (

    
    <Box>
 

    <>
    
    
    <Button key="lg" m={4} onClick={onOpen}>
          {" "}
          Ver Pedido <Badge>{props.shoppingCart.length}</Badge>
        </Button>

        <Modal onClose={onClose} size={"lg"} isOpen={isOpen}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Pedido</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              
      <Box as={motion.div} animate={{ y: 0 }} initial={{ y: -100 }}>
        {props.shoppingCart.map((course) => (
          <Box rounded="l" padding={2} shadow="md">
            <CloseButton
              onClick={() => props.remove(course)}
              display="inline"
            />
            <Text fontSize={20}>{course.plate.name}</Text>

            <CoursesGroup
              plate={course.plate}
              amount={course.amount}
              courseId={course.courseId}
              display="inline"
            />

            <Stat>
              <StatNumber>${course.amount * course.plate.price}</StatNumber>
              <StatHelpText>
                {course.amount} {course.plate.name} a ${course.plate.price} c/u
              </StatHelpText>
            </Stat>
          </Box>
        ))}

        <SimpleGrid columns={[1,2]}position="sticky" bottom="0" rounded="lg"  m="5" spacing={2}>

          <Button onClick={props.close} >Añadir más productos</Button>
          <Button colorScheme="green">Pedir (${props.shoppingCart != [] ? total() : "0"}) </Button>
        </SimpleGrid>
      </Box>
            </ModalBody>
            <ModalFooter></ModalFooter>
          </ModalContent>
        </Modal>
    </>


    </Box>
  );
};
