import { Box, Drawer, DrawerBody, DrawerContent, DrawerHeader, DrawerOverlay, Heading, HStack, Image } from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useState } from "react";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleOpened = () => {
    setIsOpen((state) => !state);
  };

  return (
		<>
	
    <Box onClick={toggleOpened}>
      <Box
        as={motion.div}
        position="fixed"
        bottom="0"
        right="0"
        boxShadow="lg"
        padding="3"
        bg="white"
        animate={{ opacity: 1 }}
        initial={{ opacity: 0 }}
				width="500px"
      >
        <Heading>shopping cart</Heading>
				{isOpen ? "opened" : "closed"}

        {isOpen && props.shoppingCart.map((item) => (
          <HStack>
            <Box as={motion.div}>
              <Image as={motion.img} src={item.imgURL} w="70" h="70" />
            </Box>
            <Box as={motion.div}>
              {item.amount} X {item.name}
            </Box>
          </HStack>
        ))}
      </Box>
    </Box>
		</>
  );
};
