import { Box, Heading, Image, Stat, StatNumber } from "@chakra-ui/react";

export const PlateIcon = (props) => {
  const { token, _id, imgURL, name, variants } = props.item;

  return (
    <Box
      key={_id}
      style={{
        height: "400px",
        backgroundSize: "100%",
      }}
      animate={{ scale: 1 }}
      initial={{ scale: 0.5 }}
      onClick={props.open}
    >
      <Box style={{ height: "70%" }}>
        <Image
          src={imgURL}
          variant="top"
          rounded="lg"
          style={{
            objectFit: "cover",
            height: "100%",
            width: "100%",
            borderRadius: 15,
          }}
        />
      </Box>

      <Box mt="1" fontWeight="semibold" as="h3" lineHeight="tight" isTruncated>
        {name}
      </Box>

      <Heading size="md">${variants[0].price}</Heading>
    </Box>
  );
};
