    //       <Card.Text>{description}</Card.Text>
    //       <h2>${price}</h2>
    //     </Card.Body>
    //   </Card>
    // );
  };

	const [selectedId, setSelectedId] = useState(null)
 
	return (
		<>
		{menuItems.map(item => (
			<motion.div layoutId={item.id} onClick={() => setSelectedId(item._id)}>
				<motion.h5>{item.name}</motion.h5>
				<motion.h2>{item.price}</motion.h2>
				<motion.h5>{item.description}</motion.h5>

			</motion.div>
		))}
		
		<AnimatePresence>
			{selectedId && (
				<motion.div layoutId={selectedId}>
					<motion.h5>{"item.name"}</motion.h5>
					<motion.h2>{"item.price"}</motion.h2>
					<motion.button onClick={() => setSelectedId(null)} />
				</motion.div>
			)}
		</AnimatePresence>
			</>
	)

  // return categories.map((cat) => (
