import { Box, Heading, HStack, Image, SimpleGrid } from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { MenuNav } from "./menuNav";
import { PlateIcon } from "./plateIcon";
import { PlateWindow } from "./plateWindow";
import { ShoppingCart } from "./shoppingCart";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const addToShoppingCart = (amount, courseId) => {
    setShoppingCart(
      shoppingCart.concat({
        plate: selectedItem,
        amount,
        extra: null,
        courseId,
      })
    );
    setSelectedItem(null);
  };

  const remove = (course) => {
    setShoppingCart(shoppingCart.filter((aCourse) => aCourse != course));
  };

  const categories = menuItems.map((item) => item.category);

  return (
    <Box bg="#f1f2e4">
      <MenuNav />
      <Box mb="5">{categories}
      wdqwjiodjwqoi</Box>
      <SimpleGrid columns={[1, 2, 3]} spacing="40px">
        {menuItems.map((item) =>
          selectedItem == item ? (
            <AnimatePresence>
              <PlateWindow
                setSelectedItem={setSelectedItem}
                item={selectedItem}
                addToShoppingCart={addToShoppingCart}
              />
              <motion.div height="400" transition={{ delay: 0 }} />
            </AnimatePresence>
          ) : (
            <AnimatePresence>
              <PlateIcon item={item} setSelectedItem={setSelectedItem} />
            </AnimatePresence>
          )
        )}
      </SimpleGrid>
      {shoppingCart != [] && (
        <ShoppingCart shoppingCart={shoppingCart} remove={remove} />
      )}
    </Box>
  );
};
