import {
  Text,
  CloseButton,
  Stat,
  StatNumber,
  StatHelpText,
  SimpleGrid,
  Badge,
  Box,
  Button,
  Heading,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  toast,
  Tag,
  Stack,
  HStack,
  Tooltip,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { calculateCoursePrice } from "../../scripts/calculateCoursePrice";
import { fullCourseData } from "../../scripts/fullCourseData";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCartLine = (props) => {
  const [completeCourse, setCompleteCourse] = useState({});

  useEffect(async () => {
    setCompleteCourse(await fullCourseData(props.course));
  }, []);

  const {
    amount,
    special,
    courseMoment,

    aditionalOptionsConfiguration,
    variant,
    menuItem,
    extras,
    price,
  } = completeCourse;

  return (
    <Tooltip
      label="pepe"
      // label={`${amount} ${menuItem.name} ${variant.name} ($${variant.price})
      //         ${extras && extras.map((extra) => `+ ${extra.name} ($${extra.price})`)}
      //         ${special}
      // `}
    >
      <Box rounded="l" padding={2} shadow="md">

        {JSON.stringify(completeCourse)}

        <CloseButton
          onClick={() => props.remove(props.course)}
          display="inline"
        />
        <Text fontSize={20}>
          {amount} {menuItem.name}
        </Text>
        <HStack spacing="2">
          <Text display="inline" fontSize={17} color="gray">
            {variant.name}
          </Text>
          {special && <Tag> {special}</Tag>}

          {extras.map((extra) => (
            <Tag>{extra.name}</Tag>
          ))}
        </HStack>

        <Text fontSize={15}></Text>
        <CoursesGroup plate={menuItem} amount={amount} display="inline" />

        <Stat>
          <StatNumber>${price}</StatNumber>
          <StatHelpText></StatHelpText>
        </Stat>
      </Box>
    </Tooltip>
  );
};

export const ShoppingCart = (props) => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const total = () => {
    let total = 0;
    props.shoppingCart
      .map((course) => calculateCoursePrice(course))
      .forEach((x) => (total += x));
    return total;
  };

  return (
    <>
      <Button key="lg" m={4} onClick={onOpen} colorScheme="green">
        Ver Pedido ({props.shoppingCart.length})
      </Button>

      {isOpen && (
        <Modal onClose={onClose} size={"lg"} isOpen={isOpen}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Pedido</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Box as={motion.div}>
                {props.shoppingCart.map((course) => (
                  <ShoppingCartLine course={course} remove={props.remove} />
                ))}

                <SimpleGrid
                  columns={[1, 2]}
                  position="sticky"
                  bottom="1vh"
                  rounded="lg"
                  m="5"
                  spacing={2}
                >
                  <Button onClick={onClose}>Añadir más productos</Button>
                  <Button colorScheme="green">
                    Pedir (${props.shoppingCart != [] ? total() : "0"}){" "}
                  </Button>
                </SimpleGrid>
              </Box>
            </ModalBody>
            <ModalFooter></ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </>
  );
};
