import { Badge, Box, Heading, Image } from "@chakra-ui/react";
import { motion } from "framer-motion";

export const PlateIcon = (props) => {
  const { token, _id, imgURL, name, price } = props.item;

  return (
    <Box
      as={motion.div}
			layoutId="expandableMenuItem"
			key={_id}
      style={{
        height: "400px",
        backgroundSize: "100%",
      }}
      onClick={() => props.setSelectedItem(props.item)}
      animate={{ scale: 1 }}
      initial={{ scale: 0.5 }}
			
    >
      <Box as={motion.div} style={{ height: "70%" }} layoutId={_id + "img"} >
        <Image
          as={motion.img}
					
          src={imgURL}
          variant="top"
          rounded="lg"
          style={{
            objectFit: "cover",
            height: "100%",
            width: "100%",
            borderRadius: 15,
          }}
        />
      </Box>
      <Box as={motion.div} layoutId={name}>
        <Heading size="md" m="2">
          {name} <Badge colorScheme="green">${price}</Badge>
        </Heading>
      </Box>
    </Box>
  );
};
