import { useEffect, useState } from 'react';
import {Command} from './components/Command'

function App() {

  const [lastUpdate, setLastUpdate] = useState(null)

  const Clock = () => {
    const [time, setTime] = useState(0)

    const incrementTime = () => setTime(time+1)

    const tick = () => {
      setTimeout(() => {
        incrementTime()
        console.log(time)
        tick()
      }, 1000)
    }

    useEffect(tick, [])

    return <h3>v</h3>
  }

  const Commands = () => {

    const [commands, setCommands] = useState(null)

    const fetchData = () => {

      setTimeout(() => {
        fetch("http://localhost:5000/api/command/all", {method: "GET"})
        .then(res => res.json())
        .then( res => setCommands(res))
        .then(() => {
          fetchData()

        })
      }, 10000);
    }
    useEffect(() => {
      fetchData() 
    } , [])
    return (
      commands ? commands.map(command => <Command  command={command}/>) 
      :       <button onClick={fetchData} >Fetch</button>

      )

  }



  return (
    <div className="App">
      <h1>Comander</h1>
        <Clock />
        <Commands />
    </div>
  );
}

export default App;
