import {
  Box,
  Text,
  Badge,
  Heading,
  CloseButton,
  Button,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleOpened = () => setIsOpen((state) => !state);

  const total = () => {
    let total = 0;
    props.shoppingCart
      .map((course) => course.amount * course.plate.price)
      .forEach((x) => (total += x));
    return total;
  };

  useEffect(() => {
    setIsOpen(true);
  }, [props.shoppingCart]);

  return (
    <>
      <Box>
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
          width="500px"
          borderRadius="lg"
          maxH="100%"
          overflowY="scroll"
        >
          <Text fontSize="30" onClick={toggleOpened}>
            Shopping Cart
          </Text>
          {isOpen && (
            <motion.div animate={{ y: 0 }} initial={{ y: -100 }}>
              {props.shoppingCart.map((course) => (
                <Box rounded="l" padding={2} shadow="md">
                  <CloseButton
                    onClick={() => props.remove(course)}
                    display="inline"
                  />
                  <Text fontSize={20}>{course.plate.name}<Badge>{course.amount} X ${course.plate.price}</Badge></Text>



                  <CoursesGroup
                    plate={course.plate}
                    amount={course.amount}
                    courseId={course.courseId} display="inline"
                  />

                  <Button  display="inline"> +</Button>
                  <Heading display="inline" fontSize="20">{course.amount}</Heading>
                  <Button  display="inline"> -</Button>
                  <Heading size="md" >
                    ${course.amount * course.plate.price}
                  </Heading>
                </Box>
              ))}
              <Heading size="lg">
                Total ${props.shoppingCart != [] ? total() : "0"}
              </Heading>
            </motion.div>
          )}
        </Box>
      </Box>
    </>
  );
};
