import { Box, Heading, Image, Text } from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";

const LastPlateIcon = (props) => (
  <PlateIcon plate={props.plate} marginLeft="0" cid={props.cid} />
);

const PlateIcon = (props) => (
    // <Box as={motion.div}  >
      <Image
        as={motion.img}
        layoutId={props.cid}
        borderRadius="full"
        boxSize="40px"
        src={props.plate.imgURL}
        alt={props.plate.name}
        fit="cover"
        shadow="sm"
        display="inline"
        marginLeft="-30"
        speed

      />
    // </Box>
);

const Icons = (props) => {
  const { amount, courseId, plate } = props;

  let icons = [];
  let i = 1;
  while (i <= amount - 1) {
    icons.push(<PlateIcon plate={plate} cid={courseId+i} />);
    i++;
  }

  icons.push(<LastPlateIcon plate={plate} cid={courseId+i} />);

  return icons;
};

export const CoursesGroup = (props) => {
  const { amount, plate, courseId } = props;

  return (
    <AnimatePresence>
      <Box
        as={motion.div}
        borderWidth="1px"
        borderRadius="lg"
        margin="5"
        padding="2"
        display="block"
        layoutId={courseId}
      >
        <Text   fontSize="lg"> {plate.name} </Text>
        <Text display="inline" fontSize="lg">

          {amount} X{" "}
        </Text>
        <Box marginLeft="35" display="inline">
          <Icons amount={amount} courseId={courseId} plate={plate} />
        </Box>
        <Text display="inline" fontSize="lg"> A punto</Text>
      </Box>
    </AnimatePresence>
  );
};
