import {
  Box,
  Button,
  CloseButton,
  Heading,
  HStack,
  Text,
  Input,
  Image,
  Modal,
  ModalBody,
  ModalOverlay,
  ModalContent,
  VStack,
  Badge,
  RadioGroup,
  Stack,
  Radio,
  Checkbox,
} from "@chakra-ui/react";
import { useState } from "react";
import { calculateCoursePrice } from "../../scripts/calculateCoursePrice";
import { CoursesGroup } from "./courseGroup";
import { DietTags } from "./dietTags";

export const PlateWindow = (props) => {
  const {
    _id,
    name,
    token,
    description,
    category,
    imgURL,
    posibleCourseMoments,
    tags,
    variants,
    extras,
    aditionalOptions,
  } = props.item;
  const [courseId, setCourseId] = useState(Math.random());

  const [amount, setAmount] = useState(1);
  const [special, setSpecial] = useState("");
  const [checkedExtras, setCheckedExtras] = useState([]);
  const [selectedVariant, setSelectedVariant] = useState(
    props.item.variants[0]
  );
  const [courseMoment, setCourseMoment] = useState(
    props.item.posibleCourseMoments[0]
  );

  const handleExtra = (event) => setSpecial(event.target.value);

  const setNewCourseId = () => {
    setCourseId(Math.random());
    setAmount(1);
  };

  const increment = () => setAmount((amount) => amount + 1);

  const decrement = () =>
    setAmount((amount) => {
      if (amount > 1) return amount - 1;
      else return amount;
    });

  const course = {
    plateId: props.item._id,
    amount,
    variant: selectedVariant,
    extras: checkedExtras,
    courseId,
    selectedType: courseMoment,
    extra: special,
  };

  const addToCart = () => {
    props.addToShoppingCart(course);
    setNewCourseId();
  };

  const BottomFloatingButtonGroup = () => (
    <HStack
      position="sticky"
      bottom="1vh"
      bg="white"
      p="3"
      m="5"
      spacing={2}
      rounded="lg"
      shadow="lg"
    >
      <HStack>
        <Button onClick={decrement}> - </Button>
        <Heading>{amount}</Heading>
        <Button onClick={increment}>+ </Button>
      </HStack>
      <Button colorScheme="green" size="lg" onClick={addToCart}>
        Añadir {amount} al pedido ({calculateCoursePrice(course)})
      </Button>
    </HStack>
  );

  const VariantsSection = () => {
    const handleVariant = (variantName) =>
      setSelectedVariant(
        variants.find((variant) => variant.name == variantName)
      );

    return (
      <Stack spacing="5">
        <Heading size="md">Elige la opcion</Heading>
        <RadioGroup value={selectedVariant.name} onChange={handleVariant}>
          <Stack>
            {variants.map((variant) => (
              <Radio value={variant.name}>
                {variant.name} <Badge>${variant.price}</Badge>
              </Radio>
            ))}
          </Stack>
        </RadioGroup>
      </Stack>
    );
  };

  const TypesSection = () => {
    const handleType = (typeName) =>
      setCourseMoment(availableTypes.find((type) => type == typeName));


    const displayType = (type) => {
      switch (type) {
        case "main": return "Plato Principal"
        case "starter": return "Entrada"
        case "dessert": return "Postre"
        case "drink": return "Bebida"
        default: return "Por Defecto"
         
      }
    }

    return (
      <Stack spacing="5">
        <Heading size="md">Elija cuándo quiere comerlo</Heading>
        <RadioGroup value={courseMoment} onChange={handleType}>
          <Stack>
            {availableTypes.map((type) => (
              <Radio value={type}>
                {displayType(type)}
              </Radio>
            ))}
          </Stack>
        </RadioGroup>
      </Stack>
    );
  };

  const ExtrasSection = () => {
    const handleCheckBox = (extraName) => {
      const extra = extras.find((extra) => extra.name == extraName);
      checkedExtras.includes(extra)
        ? setCheckedExtras(
            checkedExtras.filter((extra) => extra.name != extraName)
          )
        : setCheckedExtras(checkedExtras.concat(extra));
    };

    return (
      <Stack spacing="5">
        <Heading size="md">Configure su pedido</Heading>
        <Stack>
          {extras.map((extra) => (
            <Checkbox
              value={extra.name}
              isChecked={checkedExtras.includes(extra)}
              onChange={() => handleCheckBox(extra.name)}
            >
              {extra.name}{" "}
              {extra.price != 0 && (
                <Badge>
                  {extra.price > 0 && "+"} ${extra.price}
                </Badge>
              )}
            </Checkbox>
          ))}
        </Stack>
      </Stack>
    );
  };

  return (
    <Modal isOpen="true" size="xl" onClose={props.close}>
      <ModalOverlay
        css={{ backdropFilter: "blur(10px)" }}
        bg="rgba(100,100,100,0.2)"
        onClick={props.close}
      />
      <ModalContent>
        <ModalBody>
          <CloseButton onClick={props.close} />
          <VStack gap={10}>
            <Image src={imgURL} style={{ borderRadius: 10, width: "100%" }} />
            <Stack spacing="5">
              <Heading size="lg">{name}</Heading>

              <Heading size="md">${selectedVariant.price}</Heading>

              <Text>{description} </Text>

              <DietTags tags={tags} />

              {variants.length >= 2 && <VariantsSection />}

              {extras.length >= 1 && <ExtrasSection />}

              {availableTypes.length >= 2 && <TypesSection />}

              <Input
                placeholder="Pedido especial?"
                size="lg"
                value={special}
                onChange={handleExtra}
              />

              <CoursesGroup
                plate={props.item}
                amount={amount}
                courseId={courseId}
              />

              <Box border={"double"}>
                {JSON.stringify(course)}
              </Box>
            </Stack>

            <BottomFloatingButtonGroup />
          </VStack>
        </ModalBody>
      </ModalContent>
    </Modal>
  );
};
