import { Box, Heading, HStack, Image, SimpleGrid, Text } from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { MenuNav } from "./menuNav";
import { PlateIcon } from "./plateIcon";
import { PlateWindow } from "./plateWindow";
import { ShoppingCart } from "./shoppingCart";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const addToShoppingCart = (amount, courseId) => {
    setShoppingCart(
      shoppingCart.concat({
        plate: selectedItem,
        amount,
        extra: null,
        courseId,
      })
    );
    setSelectedItem(null);
  };

  const remove = (course) => {
    setShoppingCart(shoppingCart.filter((aCourse) => aCourse != course));
  };

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];
  const categories = categoryNames.map((name) => {
    return { name, items: menuItems.filter((item) => item.category === name) };
  }).filter(cat => cat.name );

  const Category = (props) => {
    const { name, items } = props.category;
    return (
      <>
        <h1>{name}</h1>
        <SimpleGrid columns={[1, 2, 3]} spacing="40px">
          {items.map((item) =>
            selectedItem == item ? (
              <AnimatePresence>
                <PlateWindow
                  setSelectedItem={setSelectedItem}
                  item={selectedItem}
                  addToShoppingCart={addToShoppingCart}
                />
                <motion.div height="400" transition={{ delay: 0 }} />
              </AnimatePresence>
            ) : (
              <AnimatePresence>
                <PlateIcon item={item} setSelectedItem={setSelectedItem} />
              </AnimatePresence>
            )
          )}
        </SimpleGrid>
      </>
    );
  };

  return (
    <>
      <MenuNav />
      <Box bg="#f1f2e4" pt="20">
        <Box>{categories.map(cat => (<Box><Heading>{cat.name}</Heading><Text>{JSON.stringify(cat.item)}</Text></Box>))}</Box>
        {/* {categories.map((cat) => (
          <Category categories={cat} />
        ))} */}
        {shoppingCart != [] && (
          <ShoppingCart shoppingCart={shoppingCart} remove={remove} />
        )}
      </Box>
    </>
  );
};
