import { Box, Heading, HStack } from "@chakra-ui/react";

export const ShoppingCart = () => {
  return (
    <Box
      as={motion.div}
      position="fixed"
      bottom="0"
      right="0"
      boxShadow="lg"
      padding="3"
      bg="white"
      animate={{ opacity: 1 }}
      initial={{ opacity: 0 }}
    >
      <Heading>shopping cart</Heading>

      {shoppingCart.map((item) => (
        <HStack>
          <Box as={motion.div}>
            <Image as={motion.img} src={item.imgURL} w="70" h="70" />
          </Box>
          <Box as={motion.div}>
            {item.amount} X {item.name}
          </Box>
        </HStack>
      ))}
    </Box>
  );
};
