import React from "react";
import {
  IconButton,
  Box,
  CloseButton,
  Flex,
  useColorModeValue,
  Link,
  Drawer,
  DrawerContent,
  Text,
  useDisclosure,
  MenuIcon,
  Button,
  DrawerCloseButton,
  Container,
} from "@chakra-ui/react";
import { AddIcon, HamburgerIcon } from "@chakra-ui/icons";

export default function MenuSideBar({ BodyContents, SideBarContents }) {

  BodyContents
  SideBarContents

  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
    <Box minH="100vh" bg={useColorModeValue("gray.100", "gray.900")}>
      <SidebarContent
        onClose={() => onClose}
        display={{ base: "none", md: "block" }}
      >
        <SidebarContents />
      </SidebarContent>
      <Drawer
        autoFocus={false}
        isOpen={isOpen}
        placement="left"
        onClose={onClose}
        returnFocusOnClose={false}
        onOverlayClick={onClose}
        size="full"
      >
        <DrawerContent>
          <DrawerCloseButton onClick={onClose} />
          <Box mt={5}>
            <SidebarContent onClose={onClose}>
              <SidebarContents />
            </SidebarContent>
          </Box>
        </DrawerContent>
      </Drawer>
      {/* mobilenav */}
      <MobileNav
        display={{ base: "flex", md: "none" }}
        onOpen={onOpen}
      ></MobileNav>
      <Box ml={{ base: 0, md: 60 }} p={[5, 20]}>
        <BodyContents />
      </Box>
    </Box>
  );
}

const SidebarContent = ({ children, onClose, ...rest }) => {
  return (
    <Box
      bg={useColorModeValue("white", "gray.900")}
      borderRight="1px"
      borderRightColor={useColorModeValue("gray.200", "gray.700")}
      w={{ base: "full", md: 60 }}
      pos="fixed"
      h="full"
      m="5"
      rounded="lg"
      {...rest}
      p="6"
    >
      <Flex alignItems="center" justifyContent="space-between">
        <Container onClick={onClose}>{children}</Container>
      </Flex>
    </Box>
  );
};

const MobileNav = ({ children, onOpen, ...rest }) => {
  return (
    <Flex
      ml={{ base: 0, md: 60 }}
      px={{ base: 4, md: 24 }}
      height="20"
      alignItems="center"
      bg={useColorModeValue("white", "gray.900")}
      borderBottomWidth="1px"
      borderBottomColor={useColorModeValue("gray.200", "gray.700")}
      justifyContent="flex-start"
      {...rest}
    >
      <Button
        onClick={onOpen}
        leftIcon={<HamburgerIcon />}
        variant={"outline"}
        w="100%"
        size={"lg"}
      >
        Categor√≠as
      </Button>
    </Flex>
  );
};
