import {
  Badge,
  Box,
  Button,
  Heading,
  HStack,
  Image,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalOverlay,
  SimpleGrid,
  Text,
  Wrap,
  WrapItem,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { CloseButton } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <SimpleGrid columns={[1, 2, 3]} spacing="40px">
        {menuItems.map((item) => (
          <Box
            as={motion.div}
            style={{
              height: "400px",
              backgroundSize: "100%",
            }}
            onClick={() => setSelectedItem(item)}
            layoutId={item.token}
          >
            <Box as={motion.div} layoutId={item._id} style={{ height: "70%" }}>
              <Image
                as={motion.img}
                src={item.imgURL}
                variant="top"
                className="rounded-lg "
                style={{
                  objectFit: "cover",
                  height: "100%",
                  width: "100%",
                  borderRadius: 15,
                }}
              />
            </Box>
            <Box as={motion.div} layoutId={item.name}>
              <h4 style={{ textAlign: "center", margin: 10 }}>
                {item.name}{" "}
                <Badge pill as={motion.span} layoutId={item.price}>
                  {" "}
                  ${item.price}
                </Badge>
              </h4>
            </Box>
          </Box>
        ))}
      </SimpleGrid>
      <AnimatePresence>
        {selectedItem && (
          <>
            <Box
              as={motion.div}
              layoutId={selectedItem.token}
              position={"fixed"}
              top={0}
              backgroundColor="white"
              boxShadow="dark-lg"
              rounded="xl"
              padding="3"
            >
              <Box>
                <CloseButton  onClick={()=>setSelectedItem(null)}/>
                <Wrap spacing={2} margin="10">
                  <Box>
                    <WrapItem as={motion.div} layoutId={selectedItem._id}>
                      <Image
                        as={motion.img}
                        src={selectedItem.imgURL}
                        rounded="xl"
                      />
                    </WrapItem>
                  </Box>
                  <Box margin="4">
                    <Box as={motion.div} >
                      <Heading size="lg">{selectedItem.name}</Heading>
                    </Box>
                    <Text marginTop={10}>{selectedItem.description}</Text>
                    <h2>
                      <Box as={motion.div} >
                        <Badge pill>${selectedItem.price}</Badge>
                      </Box>
                    </h2>
                    <Button colorScheme="blue">Button</Button>
                  </Box>
                </Wrap>
              </Box>
            </Box>
          </>
        )}
      </AnimatePresence>
    </>
  );
};
