import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Card, Modal } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const Plate = (props) => {
    const { name, description, price, imgURL } = props.plate;

    // return (
    //   <Card style={{ width: 500 }}>
    //     <Card.Img variant="top" src={imgURL} />

    //     <Card.Body>
    //       <Card.Title>{name}</Card.Title>
    //       <Card.Text>{description}</Card.Text>
    //       <h2>${price}</h2>
    //     </Card.Body>
    //   </Card>
    // );
  };

  const [selectedItem, setSelectedId] = useState(null);

  return (
    <>
      {menuItems.map((item) => (
        <motion.div
          layoutId={item._id}
          onClick={() => setSelectedId(item)}
          style={{
						width: 500,
						margin: 20,
						borderRadius: 20,
						top: "50%",
						left: "50%",
						zIndex: 1,
						padding: 20
          }}
        >

          <motion.h5>{item.name}</motion.h5>
          <motion.h2>{item.price}</motion.h2>
          <motion.h5>{item.description}</motion.h5>
        </motion.div>
      ))}

      <AnimatePresence>
			
				{selectedItem && <h1>is selected</h1>}

        {selectedItem && (
          <motion.div
            layoutId={selectedItem._id}
            style={{
							width: "300px",
							height: "300px",
							position: "fixed",
							bottom: 0,
							right: 0,
							marginRight: "50%",
							marginBottom: "50%",
            }}
          >
            <motion.button onClick={() => setSelectedId(null)} >Close</motion.button>
            <motion.h5>{selectedItem.name}</motion.h5>
            <motion.h2>{selectedItem.price}</motion.h2>
						<motion.h5>{selectedItem.description}</motion.h5>

          </motion.div>
        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
