import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Button, Card, Container, Modal } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <Container className="col-12">
        {menuItems.map((item) => (
          <Card
            as={motion.div}
            style={{
              // height: "22rem",
              float: "left",
              borderRadius: 10,
              margin: 20,
              // background: `url("${item.imgURL}") no-repeat`,
							backgroundSize: "100%"

            }}
            layoutId={item._id}
            onClick={() => setSelectedItem(item)}
            className="bg-dark text-white col-5 "
            sm
          >
            <Card.Img as={motion.img} src={item.imgURL} variant="top" style={{maxHeight: "40%"}}/>
            <Card.Body as={motion.div}>
              <Card.Title
                as={motion.h2}
                style={{
                  textShadow:
                    "px 3px 0 #969696, 1px 13px 5px #aba8a8, 0px 0px 18px #000000",
                }}
              >
                {item.name} -Â¿
              </Card.Title>
              <Card.Text as={motion.p}>${item.price}</Card.Text>

                
       
            </Card.Body>
          </Card>
        ))}
      </Container>
      <AnimatePresence>
        {selectedItem && (
          <>
            <motion.div
              style={{
                width: "100%",
                height: "100%",
                backgroundColor: "white",
                position: "fixed",
                top: 0,
              }}
							initial={{opacity: 0}}
							animate={{opacity: 0.5}}
              onClick={() => setSelectedItem(null)}
            />
            <Card
              as={motion.div}
              style={{ position: "fixed", top: "0%", left: "0%", margin: 20 }}
              layoutId={selectedItem._id}
              onClick={() => setSelectedItem(selectedItem)}
							className="shadow-lg "
            >
              <Card.Img
                as={motion.img}
                variant="top"
                src={selectedItem.imgURL}
              />
              <Card.Body as={motion.div}>
                <Card.Title as={motion.h2}>{selectedItem.name}</Card.Title>
                <Card.Text as={motion.p}>{selectedItem.description}</Card.Text>
                <Button as={motion.button} variant="primary">
                  ${selectedItem.price}
                </Button>
              </Card.Body>
            </Card>
          </>
        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
