import { Box, Text, Badge, Heading } from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleOpened = () => {
    setIsOpen((state) => !state);
  };

  return (
    <>
      <Box onClick={toggleOpened}>
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
          width="500px"
          borderRadius="lg"
          overflowY="scroll"
        >
          <Text fontSize="30">Shopping Cart</Text>
          {isOpen && (
            <motion.div animate={{ y: 0 }} initial={{ y: -100 }}>
              {props.shoppingCart.map((course) => (
                <Box borderBottom="solid gray 1px " rounded="l" padding={2}>
                  <CoursesGroup
                    plate={course.plate}
                    amount={course.amount}
                    courseId={course.courseId}
                  />
                  <Text fontSize={20}>
                    {" "}
                    {course.amount} X {course.plate.name}{" "}
                    <Badge> ${course.plate.price}</Badge>{" "}
                  </Text>
                  <Heading size="md">
                    ${course.amount * course.plate.price}
                  </Heading>
                </Box>
              ))}
              <Heading size="lg">
                Total $
                {props.shoppingCart
                  .map((course) => course.plate.price * course.amount)
                  .reduce((sum, x) => (sum += x))}
              </Heading>
              {JSON.stringify()}
            </motion.div>
          )}
        </Box>
      </Box>
    </>
  );
};
