import {
  Badge,
  Box,
  Button,
  Heading,
  Image,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalOverlay,
  SimpleGrid,
  Text,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <SimpleGrid columns={[1, 2, 3]} spacing="40px">
        {menuItems.map((item) => (
          <Box
            as={motion.div}
            style={{
              height: "400px",
              backgroundSize: "100%",
            }}
            onClick={() => setSelectedItem(item)}
            layoutId={item.token}
          >
            <Box as={motion.div} layoutId={item._id} style={{ height: "70%" }}>
              <Image
                src={item.imgURL}
                variant="top"
                className="rounded-lg "
                style={{
                  objectFit: "cover",
                  height: "100%",
                  width: "100%",
                  borderRadius: 15,
                }}
              />
            </Box>
            <Box as={motion.div} layoutId={item.name}>
              <h4 style={{ textAlign: "center", margin: 10 }}>
                {item.name}{" "}
                <Badge pill as={motion.span} layoutId={item.price}>
                  {" "}
                  ${item.price}
                </Badge>
              </h4>
            </Box>
          </Box>
        ))}
      </SimpleGrid>
      <AnimatePresence>
        {selectedItem && (
          <>

            <Modal
              isOpen={true}
              onClose={() => setSelectedItem(null)}
              as={motion.div}
              layoutId={selectedItem.token}
              motionPreset ="none"
            >
              <ModalOverlay />
              <ModalContent layoutId={selectedItem._id} as={motion.div}>
                <ModalCloseButton />
                <ModalBody>
                  <SimpleGrid columns={[1, 2]} spacing={10}>
                    <Box>
                      <Box as={motion.div} layoutId={selectedItem.imgURL}>
                        <Image
                          
                          as={motion.img}
                          src={selectedItem.imgURL}
                          style={{ borderRadius: 20 }}
                        />
                      </Box>
                    </Box>
                    <Box>
                      <Box as={motion.div} layoutId={selectedItem.name}>
                        <Heading size="lg">{selectedItem.name}</Heading>
                      </Box>
                      <Text marginTop={10}>{selectedItem.description}</Text>
                      <h2>
                        <Box as={motion.div} layoutId={selectedItem.price}>
                          <Badge pill>${selectedItem.price}</Badge>
                        </Box>
                      </h2>
                      <Button colorScheme="blue">Button</Button>
                    </Box>
                  </SimpleGrid>
                </ModalBody>
              </ModalContent>
            </Modal>
          </>
         )} 
      </AnimatePresence>
    </>
  );
};
