import {
  Box,
  Drawer,
  DrawerBody,
  DrawerContent,
  DrawerHeader,
  DrawerOverlay,
  Heading,
  HStack,
  Image,
  Text,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleOpened = () => {
    setIsOpen((state) => !state);
  };


  return (
    <>
      <Box onClick={toggleOpened}>
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
          width="500px"
          borderRadius="lg"
        >
          <Text fontSize="30">Shopping Cart</Text>
          {isOpen && (
            <motion.div animate={{ y: 0 }} initial={{ y: -100 }}>
              {props.shoppingCart.map((course) =>( 
                <>
              <CoursesGroup plate={course.plate} amount={course.amount} courseId={course.courseId}/>
                <Text fontSize={30}> {course.plate.name} </Text>
                </>
              ))}
            </motion.div>
          )}
        </Box>
      </Box>
    </>
  );
};
