import {
  Box,
  Button,
  CloseButton,
  Heading,
  HStack,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  SimpleGrid,
  Text,
  Input,
  WrapItem,
  Image,
  IconButton,
  GridItem,
  Modal,
  ModalBody,
  ModalOverlay,
  ModalContent,
  VStack,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import { useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const PlateWindow = (props) => {
  const { _id, imgURL, name, description, price } = props.item;
  const [courseId, setCourseId] = useState(Math.random());

  const [amount, setAmount] = useState(1);
  const setNewCourseId = () => {
    setCourseId(Math.random());
    setAmount(1);
  };

  const increment = () => setAmount((amount) => amount + 1);

  const decrement = () =>
    setAmount((amount) => {
      if (amount > 1) return amount - 1;
      else return amount;
    });

  return (
    <>
      <Modal isOpen="true" size="xl" width="90vw" onClose={props.close}>
        <ModalOverlay
          css={{ backdropFilter: "blur(10px)" }}
          bg="rgba(100,100,100,0.2)"
          onClick={props.close}
        />
        <ModalContent>
          <ModalBody>
            <CloseButton onClick={props.close} />
            <VStack gap={10} columns={[1, 2]}>
              <Box colSpan="1">
                <Image
                  as={motion.img}
                  src={imgURL}
                  style={{ borderRadius: 10, width: "100%" }}
                  layoutId={"img" + _id}
                />
              </Box>
              <Box colSpan="2">
                <Box margin="4">
                  <VStack spacing="5">
                    <Box as={motion.div} layoutId={"name" + _id}>
                      <Heading size="lg">{name}</Heading>
                    </Box>
                    <Heading as={motion.h2} layoutId={"price" + _id} size="md">
                      ${price}
                    </Heading>
                    <Text>{description}</Text>

                    <Input placeholder="Pedido especial?" size="lg" />

                    <CoursesGroup
                      plate={props.item}
                      amount={amount}
                      courseId={courseId}
                    />
                  </VStack>

                  <HStack
                    columns={[1, 2]}
                    position="sticky"
                    bottom="1vh"
                    rounded="lg"
                    m="5"
                    spacing={2}
                  >
                    <HStack>
                      <Button onClick={decrement}> - </Button>
                      <Heading>{amount}</Heading>
                      <Button onClick={increment}>+ </Button>
                    </HStack>
                    <Button
                      colorScheme="green"
                      size="lg"
                      onClick={() => {
                        props.addToShoppingCart(props.item, amount, courseId);
                        setNewCourseId();
                      }}
                    >
                      Añadir {amount} al pedido (${price * amount})
                    </Button>
                  </HStack>
                </Box>
              </Box>
            </VStack>
          </ModalBody>
        </ModalContent>
      </Modal>
    </>
  );
};
