import React, { useEffect, useState } from "react";
import { unstable_renderSubtreeIntoContainer } from "react-dom/cjs/react-dom.development";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [... new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames.map((name) => {
		if(!name) return
    return {
      name: name,
      items: menuItems.filter((item) => item.category == name),
    };
  });


  return categories.map((cat) => (
    <>
      <h1>{cat.name}</h1>
      {cat.items.map((item) => (
        <div>
          <h3>{item.name}</h3>
          <p>{item.description}</p>
          <h4>${item.price}</h4>
          <img src={item.imgURL} />
        </div>
      ))}
    </>
  ));
};
