import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const Plate = (props) => {

		const {name, description, price, imgURL} = props.plate

		return(
			<Card>
				  <Card.Img variant="top" src={imgURL} />

      <Card.Body>
        <Card.Title>{name}</Card.Title>
				<Card.Text>
        
					{description}
					

				</Card.Text>
        ${price}
      </Card.Body>
    </Card>
  );
}
	
  return categories.map((cat) => (
    <>
      <h1>{cat.name}</h1>
      {cat.items.map((item) => (
        <Plate plate={item} />
      ))}
    </>
  ));
};
