import { useEffect } from "react";
import { Card, ListGroup } from "react-bootstrap";
import { useState } from "react/cjs/react.development";

export const Command = (props) => {
  const { kitchen, date, time, waiter, table, _id } = props.command;

  const [courses, setCourses] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/course/command/${_id}`)
      .then((res) => res.json())
      .then((res) => setCourses(res));
  });

  const mainCourses = courses.filter((course) => !course.isStarter);
  const starterCourses = courses.filter((course) => course.isStarter);

  const Course = (props) => {
    let { plateName, plateToken, extra, status, amount } = props.course;

    if (!extra) extra = "";

    return (
      <ListGroup.Item>
        {" "}
        <div style={{ fontFamily: "helvetica" }}>
          {`${amount} | ${plateName}`}
          {extra != " " ? <div>{extra}</div> : null}
        </div>
      </ListGroup.Item>
    );
  };

  const TKTCourse = (props) => {
    let { plateToken, extra, status, amount } = props.course;

    if (!extra) extra = "";

    return (
      <div >
        <div style={{fontSize: 20}}>
          {`${amount}  ${plateToken}`}
          {extra != " " ? <div style={{fontSize: 15}}>{"  \t"}  {extra}</div> : null}
        </div>
      </div>
    );
  };

  return (
    <div
      style={{
        display: "float",
        width: 300,
        padding: 20,
        margin: 20,
        boxShadow: "#0000004a 1px 2px 20px 0px",
        fontFamily: 'monospace',
        fontSize: 20
      }}
    >
      <div style={{fontWeight: 'bold', fontSize: 21}}> COCINA: {kitchen} </div>
      <div style={{  display: 'flex',  justifyContent: 'space-between'}}>
        <div > Fecha:  {date} </div>

        <div > Hora:  {time} </div>
      </div>
      <div style={{  display: 'flex',  justifyContent: 'space-between'}}>
      <div > Mesa:  {table} </div>
      <div > Mozo:  {waiter} </div>

      </div>
      ----------------------------------
      <div>
        {starterCourses.map((course) => (
          <TKTCourse course={course} />
        ))}
        {starterCourses!="" ? "1 ---------------------": null}
        {mainCourses.map((course) => (
          <TKTCourse course={course} />
        ))}
      </div>
    </div>
  );

  // return (
  //   <Card
  //     bg="light"
  //     key={_id}
  //     style={{
  //       margin: 10,
  //       width: "fit-content",
  //       float: "left",
  //       display: "inline",
  //     }}
  //     className="mb-2"
  //   >
  //     <Card.Body>
  //       <ListGroup variant="light">
  //         <ListGroup.Item> Fecha: {date} </ListGroup.Item>
  //         <ListGroup.Item> Hora: {time} </ListGroup.Item>
  //         <ListGroup.Item> Mozo: {waiter} </ListGroup.Item>
  //         <ListGroup.Item> Mesa: {table} </ListGroup.Item>
  //         <ListGroup.Item> Cocina: {kitchen} </ListGroup.Item>
  //       </ListGroup>
  //       <div style={{}}>
  //         {starterCourses ? "Entradas" : null}
  //         <ListGroup variant="light">
  //           {starterCourses.map((course) => (
  //             <TKTCourse course={course} />
  //           ))}
  //         </ListGroup>

  //         {mainCourses ? "Principales" : null}
  //         <ListGroup variant="light">
  //           {mainCourses.map((course) => (
  //             <TKTCourse course={course} />
  //           ))}
  //       </div>
  // );
};
