import { Box, Drawer, DrawerBody, DrawerContent, DrawerHeader, DrawerOverlay, Heading, HStack, Image } from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useState } from "react";

export const ShoppingCart = (props) => {
  const [isOpened, setIsOpened] = useState(false);

  const toggleOpened = () => {
    setIsOpened((state) => !state);
  };

  return (
		<>
		
		<Drawer placement={placement} onClose={onClose} isOpen={isOpen}>
		<DrawerOverlay />
		<DrawerContent>
			<DrawerHeader borderBottomWidth='1px'>Basic Drawer</DrawerHeader>
			<DrawerBody>
				<p>Some contents...</p>
				<p>Some contents...</p>
				<p>Some contents...</p>
			</DrawerBody>
		</DrawerContent>
	</Drawer>


    <Box onClick={toggleOpened}>
      <Box
        as={motion.div}
        position="fixed"
        bottom="0"
        right="0"
        boxShadow="lg"
        padding="3"
        bg="white"
        animate={{ opacity: 1 }}
        initial={{ opacity: 0 }}
      >
        <Heading>shopping cart</Heading>
				{isOpened ? "opened" : "closed"}

        {props.shoppingCart.map((item) => (
          <HStack>
            <Box as={motion.div}>
              <Image as={motion.img} src={item.imgURL} w="70" h="70" />
            </Box>
            <Box as={motion.div}>
              {item.amount} X {item.name}
            </Box>
          </HStack>
        ))}
      </Box>
    </Box>
		</>
  );
};
