import {
  Box,
  Text,
  Badge,
  Heading,
  CloseButton,
  Button,
  Stat,
  StatLabel,
  StatNumber,
  StatHelpText,
  SimpleGrid,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCart = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleOpened = () => setIsOpen((state) => !state);

  const total = () => {
    let total = 0;
    props.shoppingCart
      .map((course) => course.amount * course.plate.price)
      .forEach((x) => (total += x));
    return total;
  };

  useEffect(() => {
    setIsOpen(true);
  }, [props.shoppingCart]);

  return (
    <Box>
      <Box as={motion.div} animate={{ y: 0 }} initial={{ y: -100 }}>
        {props.shoppingCart.map((course) => (
          <Box rounded="l" padding={2} shadow="md">
            <CloseButton
              onClick={() => props.remove(course)}
              display="inline"
            />
            <Text fontSize={20}>{course.plate.name}</Text>

            <CoursesGroup
              plate={course.plate}
              amount={course.amount}
              courseId={course.courseId}
              display="inline"
            />

            <Stat>
              <StatNumber>${course.amount * course.plate.price}</StatNumber>
              <StatHelpText>
                {course.amount} {course.plate.name} a ${course.plate.price} c/u
              </StatHelpText>
            </Stat>
          </Box>
        ))}

        <SimpleGrid columns={[1,2]}position="sticky" bottom="0" rounded="lg"  m="5" spacing={2}>

          <Button onClick={close}>Añadir más </Button>
          <Button colorScheme="green">Pedir (${props.shoppingCart != [] ? total() : "0"}) </Button>
        </SimpleGrid>
      </Box>
    </Box>
  );
};
