import { useEffect, useState } from 'react';
import {Command} from './components/Command'

function App() {

  const [commands, setCommands] = useState(null)
  const [lastUpdate, setLastUpdate] = useState(0)


  const clock = () =>{ 
    setTimeout(() => {
      setLastUpdate(lastUpdate+1)
      clock()}
      ,1000)}

  const fetchData = () => {

    setTimeout(() => {
      fetch("http://localhost:5000/api/command/all", {method: "GET"})
      .then(res => res.json())
      .then( res => setCommands(res))
      .then(() => {
        fetchData()
        setLastUpdate(0)

      })

    }, 5000);
  }

  useEffect(() => {
    fetchData() 
    clock()} , [])

  return (
    <div className="App">
      <h1>Comander</h1>
      <h3>Last update: {lastUpdate} seconds ago</h3>
      {commands ? commands.map(command => <Command  command={command}/>) : <></>}
      <button onClick={fetchData} >Fetch</button>
    </div>
  );
}

export default App;
