import {
  Box,
  Center,
  useColorModeValue,
  Heading,
  Text,
  Stack,
  Image,
} from "@chakra-ui/react";
import { server } from "../../config";
import { MenuItemImage } from "./menuItemImage";

export const MenuItemCard = ({ item }) => {
  const IMAGE = `${server}/images/${item._id}${item.imgURL}`;
  return (
    <Center py={12}>
      <Box
        role={"group"}
        p={6}
        maxW={"330px"}
        w={"full"}
        rounded={"lg"}
        pos={"relative"}
        zIndex={0}
   
      >
        <Box
          rounded={"lg"}
          mt={-12}
          pos={"relative"}
          height={"230px"}
          _after={{
            transition: "all .3s ease",
            content: '""',
            w: "full",
            h: "full",
            pos: "absolute",
            top: 5,
            left: 0,
            backgroundImage: `url(${IMAGE})`,
            filter: "blur(50px)",
            zIndex: -1,
          }}
          _groupHover={{
            _after: {
              filter: "blur(20px)",
	      border: "2px solid red"
            },
          }}
        >
          <Image
            src={IMAGE}
            rounded={"lg"}
            height={230}
            width={282}
            objectFit={"cover"}
          />
        </Box>
        <Stack pt={5} align={"center"}>
          <Text
            fontSize={"2xl"}
            fontFamily={"body"}
            fontWeight={500}
            display="inline"
          >
            {item.name}
          </Text>
          <Text fontWeight={"bold"} fontSize={"xl"}>
            $ {item.variants[0].price}
          </Text>
          <Stack direction={"row"} align={"center"}></Stack>
        </Stack>
      </Box>
    </Center>
  );
};
