import { useEffect } from "react";
import { useState } from "react/cjs/react.development";

export const Command = (props) => {
  const { kitchen, date, time, waiter, table, _id } = props.command;

  const [courses, setCourses] = useState([]);

  // const starterCourses = courses.filter(course => course.isStarter)

  // const mainCourses = courses.filter(course => !course.isStarter)

  useEffect(() => {
    fetch(`http://localhost:5000/api/course/command/${_id}`)
      .then((res) => res.json())
      .then((res) => setCourses(res));
  }, []);

  const mainCourses = courses.filter((course) => !course.isStarter);
  const starterCourses = courses.filter((course) => course.isStarter);

  const Course = (props) => {
    const { plateName, extra, status } = props.course;

    return (
      <div style={{fontFamily: 'helvetica'}}>
        <h5>{plateName}</h5>
        {extra}
        {status}
      </div>
    );
  };

  return (
    <div style={{ border: "solid black 1px", margin: 10 }}>
      <ul>
        <strong>Cocina</strong>: {kitchen} <br />
        <strong>Fecha</strong>: {date} <br />
        <strong>Hora</strong>: {time} <br />
        <strong>Mozo</strong>: {waiter} <br />
        <strong>Mesa</strong>: {table} <br />
        {starterCourses ? <h3>Entradas:</h3> : null}
        {starterCourses.map((course) => (
          <Course course={course} />
        ))}
        {mainCourses ? <h3>Principales:</h3> : null}
        {mainCourses.map((course) => (
          <Course course={course} />
        ))}
      </ul>
    </div>
  );
};
