import { Button, Heading, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, Textarea, useDisclosure } from "@chakra-ui/react";
import { useState } from "react";
import { newMenuItem } from "../../scripts/newMenuItem";

export const NewMenuItem = () => {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const [menuItem, setMenuItem] = useState("")

    const handleSubmit = async () => {
        const itemObj = JSON.parse(menuItem)
        console.log(itemObj)
        const response = await newMenuItem(itemObj)
    }

    const handleChange = (event) => {
        setMenuItem(event.target.value) 
    }

    return (
        <>
            <Button
                size='md'
                height='48px'
                width='200px'
                border='2px'
                borderColor='green.500' onClick={onOpen}>AÃ±adir Nuevo Plato</Button>



            <Modal isOpen={isOpen} onClose={onClose}>
                <ModalOverlay />
                <ModalContent>
                    <ModalHeader>Nuevo Plato</ModalHeader>
                    <ModalCloseButton />
                    <ModalBody>

                        <Textarea onChange={handleChange}  value={menuItem} placeholder='Here is a sample placeholder' />

                        <Button onClick={handleSubmit} >
                            Enviar
                        </Button>

                    </ModalBody>

                    <ModalFooter>
                        <Button colorScheme='blue' mr={3} onClick={onClose}>
                            Close
                        </Button>
                        <Button variant='ghost'>Secondary Action</Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>
        </>
    )

}
