import { useEffect, useState } from 'react';
import {Command} from './components/Command'

function App() {

  const [commands, setCommands] = useState(null)
  const [lastUpdate, setLastUpdate] = useState(null)




  const fetchData = () => {

    setTimeout(() => {
      fetch("http://localhost:5000/api/command/all", {method: "GET"})
      .then(res => res.json())
      .then( res => setCommands(res))
      .then(() => {
        setLastUpdate(Date.now)
        fetchData()

      })
    }, 10000);
  }

  useEffect(() => {
    fetchData() 
  } , [])

  const Clock = () => {
    const [time, setTime] = useState(0)

    useEffect(() => {
      setTimeout(() => {
        setTime(time+1)
      }, 1000)
    }, [])

    return time
  }

  const Commands = () => 
    commands ? commands.map(command => <Command  command={command}/>) 
    : <></>



  return (
    <div className="App">
      <h1>Comander</h1>
      <Clock />
      <Commands />


      <button onClick={fetchData} >Fetch</button>
    </div>
  );
}

export default App;
