  Text,
  CloseButton,
  Stat,
  StatNumber,
  StatHelpText,
  SimpleGrid,
  Badge,
  Box,
  Button,
  Heading,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  toast,
  Tag,
  Stack,
  HStack,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCartLine = (props) => {
  return props.course ? JSON.stringify(props) : "empty"
}
