import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Card, Modal } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const Plate = (props) => {
    const { name, description, price, imgURL } = props.plate;

    // return (
    //   <Card style={{ width: 500 }}>
    //     <Card.Img variant="top" src={imgURL} />

    //     <Card.Body>
    //       <Card.Title>{name}</Card.Title>
    //       <Card.Text>{description}</Card.Text>
    //       <h2>${price}</h2>
    //     </Card.Body>
    //   </Card>
    // );
  };

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <div style={{ display: "block"}}>
        {menuItems.map((item) => (
					<>

<Card as={motion.div} style={{ width: '18rem' }}             layoutId={item._id}
            onClick={() => setSelectedItem(item)}>
  <Card.Img as={motion.img} variant="top" src="holder.js/100px180" />
  <Card.Body as={motion.div}>
    <Card.Title as={motion.h2}>{item.name}</Card.Title>
    <Card.Text as={motion.p}>
		{item.description}
    </Card.Text>
    <Button as={motion.button} variant="primary">${item.price}</Button>
  </Card.Body>
</Card>




          <motion.div

            style={{
              width: 400,
              height: 300,
              backgroundImage: `url("${item.imgURL}")`,
              margin: 20,
							float: "left",
							display: "inline"
            }}
          >
            <div
              style={{
                width: "100%",
                height: "100%",
                padding: 20,
                background:
                  "linear-gradient(rgb(255 255 255), rgb(7 7 7 / 22%))",
              }}
            >
              <motion.h3>{item.name}</motion.h3>
              <motion.p>{item.description}</motion.p>
              <motion.h4></motion.h4>
            </div>
          </motion.div>
					</>

        ))}
      </div>
      <AnimatePresence>
        {selectedItem && (
          <motion.div
            style={{
              width: 400,
              height: 300,
              backgroundImage: `url("${selectedItem.imgURL}")`,
              margin: 20,
              position: "fixed",
              top: 0,
            }}
          >
            <motion.div
              style={{
                background:
                  "linear-gradient(rgb(255 255 255), rgb(7 7 7 / 22%))",
                width: "100%",
                height: "100%",
                position: "fixed",
                top: 0,
              }}
              onClick={() => setSelectedItem(null)}
            />

            <motion.div
              layoutId={selectedItem._id}
              style={{
                width: 800,
                height: 600,
                position: "fixed",
                top: 0,
                left: 0,
                background: `url("${selectedItem.imgURL}") no-repeat`,
								backgroundSize: "100%",
								margin: 100,
								padding: 20
              }}
            >

								<motion.div style={{backgroundColor: "white", padding: 20}}>

                <motion.h3>{selectedItem.name}</motion.h3>
                <motion.p>{selectedItem.description}</motion.p>
                <motion.h4>${selectedItem.price}</motion.h4>
                <motion.button>Button</motion.button>
                <motion.button>Button</motion.button>
                <motion.img src={selectedItem.imgURL} />
								</motion.div>

            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
