import {useState, useEffect} from "react"

export const  Clock = () => {
	const [counter, setTime] = useState(0)
    
	const incrementTime = () => setTime(counter+1)
    
	const tick = () => {
	  setTimeout(() => {
	    incrementTime()
	    console.log(counter)
	    tick()
	  }, 1000)
	}
    
	// useEffect(tick)
    
	return (
	<>
	<h3>{counter}</h3>)
	<button onClick={tick}>start</button>
	</>
	)
      }
