import { Box, Image, Text } from "@chakra-ui/react";
import { motion } from "framer-motion";

const LastPlateIcon = (props) => (
  <PlateIcon plate={props.plate} marginLeft="0" />
);

const PlateIcon = (props) => (
	<motion.div display="inline" 	layoutId={props.key}
	>
		{props.key + "hello"}
  <Image
    as={motion.img}
    borderRadius="full"
    boxSize="40px"
    src={props.plate.imgURL}
    alt={props.plate.name}
    fit="cover"
    shadow="md"
    display="inline"
    marginLeft="-30"
		/>
		</motion.div>
);


const Icons = () => {
	let icons = [];
	let i = 1;
	while (i <= amount - 1) {
		const key = `${courseId}-${i}`
		icons.push(<PlateIcon plate={plate}  key={key} />);
		i++;
	}
	const key = `${courseId}-${i+1}`

	icons.push(<LastPlateIcon plate={plate} key={key} />);

	return <>{icons}</>;
};

export const CoursesGroup = (props) => {
  const { amount, plate, courseId } = props;



  return (
    <Box
      borderWidth="1px"
      borderRadius="lg"
      margin="5"
      padding="2"
      display="block"
    >
      <Text display="inline" fontSize="lg">
        {amount} X{" "} 
      </Text>
      <Box marginLeft="35" display="inline">
        <Icons />

      </Box>
    </Box>
  );
};
