import {
  Text,
  CloseButton,
  Stat,
  StatNumber,
  StatHelpText,
  SimpleGrid,
  Badge,
  Box,
  Button,
  Heading,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  toast,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect } from "react";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCartLine = (props) => (
  <Box rounded="l" padding={2} shadow="md">
    <CloseButton onClick={() => props.remove(props.course)} display="inline" />
    <Text fontSize={20}>{props.course.plate.name}</Text>
    {props.course.extras.map(extra => <Badge>{extra.name}</Badge>)}

    <Text fontSize={15}>
      {props.course.variant.name}{" "}
      {props.course.extra && `(${props.course.extra})`}
      
    </Text>
    <CoursesGroup
      plate={props.course.plate}
      amount={props.course.amount}
      courseId={props.course.courseId + "shoppingCart"}
      display="inline"
    />

    <Stat>
      <StatNumber>${props.course.amount * props.course.plate.price}</StatNumber>
      <StatHelpText>
        {props.course.amount} {props.course.plate.name} a $
        {props.course.plate.price} c/u
      </StatHelpText>
    </Stat>
  </Box>
);

export const ShoppingCart = (props) => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const total = () => {
    let total = 0;
    props.shoppingCart
      .map((course) => course.amount * course.plate.price)
      .forEach((x) => (total += x));
    return total;
  };

  return (
    <>
      <Button key="lg" m={4} onClick={onOpen} colorScheme="green">
        Ver Pedido ({props.shoppingCart.length})
      </Button>

      {isOpen && (
        <Modal onClose={onClose} size={"lg"} isOpen={isOpen}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Pedido</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Box as={motion.div}>
                {props.shoppingCart.map((course) => (
                  <ShoppingCartLine course={course} remove={props.remove} />
                ))}

                <SimpleGrid
                  columns={[1, 2]}
                  position="sticky"
                  bottom="1vh"
                  rounded="lg"
                  m="5"
                  spacing={2}
                >
                  <Button onClick={onClose}>Añadir más productos</Button>
                  <Button colorScheme="green">
                    Pedir (${props.shoppingCart != [] ? total() : "0"}){" "}
                  </Button>
                </SimpleGrid>
              </Box>
            </ModalBody>
            <ModalFooter></ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </>
  );
};
