import {
  Box,
  Heading,
  HStack,
  Image,
  SimpleGrid,
  Text,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { MenuNav } from "./menuNav";
import { PlateIcon } from "./plateIcon";
import { PlateWindow } from "./plateWindow";
import { ShoppingCart } from "./shoppingCart";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));

    const categoryNames = [...new Set(menuItems.map((item) => item.category))];
    setCategories(
      categoryNames
        .map((name) => {
          return {
            name,
            items: menuItems.filter((item) => item.category === name),
          };
        })
        .filter((cat) => cat.name)
    );
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const addToShoppingCart = (amount, courseId) => {
    setShoppingCart(
      shoppingCart.concat({
        plate: selectedItem,
        amount,
        extra: null,
        courseId,
      })
    );
    setSelectedItem(null);
  };

  const remove = (course) => {
    setShoppingCart(shoppingCart.filter((aCourse) => aCourse != course));
  };

  const Category = (props) => {
    const { name, items } = props.category;
    return (
      <>
        <Box>
          <Heading as="h3">{name}</Heading>
        </Box>
        <SimpleGrid columns={[1, 2, 3]} spacing="40px">
          {items.map((item) =>
            selectedItem == item ? (
              <AnimatePresence>
                <PlateWindow
                  setSelectedItem={setSelectedItem}
                  item={selectedItem}
                  addToShoppingCart={addToShoppingCart}
                />
                <motion.div height="400" transition={{ delay: 0 }} />
              </AnimatePresence>
            ) : (
              <AnimatePresence>
                <PlateIcon item={item} setSelectedItem={setSelectedItem} />
              </AnimatePresence>
            )
          )}
        </SimpleGrid>
      </>
    );
  };

  return (
    <>
      <MenuNav />
      <Box bg="#f1f2e4" pt="20">
        {categories.map((cat) => (
          <Category category={cat} />
        ))}
        {shoppingCart == [] && (
          <ShoppingCart shoppingCart={shoppingCart} remove={remove} />
        )}
      </Box>
    </>
  );
};
