import {
  Box,
  Button,
  CloseButton,
  Heading,
  HStack,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  SimpleGrid,
  Text,
  Input,
  WrapItem,
  Image,
  IconButton,
  GridItem,
  Modal,
  ModalBody,
  ModalOverlay,
  ModalContent,
  VStack,
  Badge,
  Avatar,
  Popover,
  PopoverTrigger,
  PopoverContent,
  Tooltip,
  Tag,
  TagLabel,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import { useState } from "react";
import { CoursesGroup } from "./courseGroup";
import { DietTags } from "./dietTags";

export const PlateWindow = (props) => {
  const { _id, imgURL, name, description, price, tags } = props.item;
  const [courseId, setCourseId] = useState(Math.random());

  const [amount, setAmount] = useState(1);
  const [extra, setExtra] = useState("")

  const handleExtra = event => setExtra(event.target.value)

  const setNewCourseId = () => {
    setCourseId(Math.random());
    setAmount(1);
  };

  const increment = () => setAmount((amount) => amount + 1);

  const decrement = () =>
    setAmount((amount) => {
      if (amount > 1) return amount - 1;
      else return amount;
    });

  const addToCart = () => {
    props.addToShoppingCart({plate: props.item, amount, courseId, extra});
    setNewCourseId();
  }

  const BottomFloatingButtonGroup = () => (                  <HStack
    position="sticky"
    bottom="1vh"
    bg="white"
    p="3"
    m="5"
    spacing={2}
    rounded="lg"
    shadow="lg"
  >
    <HStack>
      <Button onClick={decrement}> - </Button>
      <Heading>{amount}</Heading>
      <Button onClick={increment}>+ </Button>
    </HStack>
    <Button
      colorScheme="green"
      size="lg"
      onClick={addToCart}
    >
      AÃ±adir {amount} al pedido (${price * amount})
    </Button>
  </HStack>)

  return (
      <Modal isOpen="true" size="xl" onClose={props.close}>
        <ModalOverlay
          css={{ backdropFilter: "blur(10px)" }}
          bg="rgba(100,100,100,0.2)"
          onClick={props.close}
        />
        <ModalContent>
          <ModalBody>
            <CloseButton onClick={props.close} />
            <VStack gap={10} >
                <Image
                  as={motion.img}
                  src={imgURL}
                  style={{ borderRadius: 10, width: "100%" }}
                  layoutId={"img" + _id}
                />
              <Box colSpan="2">
                <Box margin="4">
                  <VStack spacing="5">
                    <Box as={motion.div} layoutId={"name" + _id}>
                      <Heading size="lg">{name}</Heading>
                    </Box>
                    <Heading as={motion.h2} layoutId={"price" + _id} size="md">
                      ${price}
                    </Heading>
                    <Text>{description} </Text>
                    <DietTags tags={tags} />

                    <Input placeholder="Pedido especial?" size="lg" value={extra} onChange={handleExtra} />

                    <CoursesGroup
                      plate={props.item}
                      amount={amount}
                      courseId={courseId}
                    />
                  </VStack>


                </Box>
              </Box>
            </VStack>
          </ModalBody>
        </ModalContent>
      </Modal>
  );
};
