import {
  Text,
  CloseButton,
  Stat,
  StatNumber,
  StatHelpText,
  SimpleGrid,
  Badge,
  Box,
  Button,
  Heading,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  toast,
  Tag,
  Stack,
  HStack,
  Tooltip,
  Table,
  VStack,
  Tbody,
  Tr,
  Td,
} from "@chakra-ui/react";
import { motion } from "framer-motion";
import { useEffect, useState } from "react";
import { calculateCoursePrice } from "../../scripts/calculateCoursePrice";
import { fullCourseData } from "../../scripts/fullCourseData";
import { CoursesGroup } from "./courseGroup";

export const ShoppingCartLine = (props) => {
  const [amount, setAmount] = useState("");
  const [special, setSpecial] = useState("");
  const [courseMoment, setCourseMoment] = useState("");
  const [aditionalOptionsConfiguration, setAditionalOptionsConfiguration] =
    useState([]);
  const [variant, setVariant] = useState("");
  const [menuItem, setMenuItem] = useState({ name: "", price: 0 });
  const [extras, setExtras] = useState([]);
  const [price, setPrice] = useState(0);

  useEffect(async () => {
    const completeCourse = await fullCourseData(props.course);

    setAmount(completeCourse.amount);
    setSpecial(completeCourse.special);
    setCourseMoment(completeCourse.courseMoment);
    setAditionalOptionsConfiguration(
      completeCourse.aditionalOptionsConfiguration
    );
    setVariant(completeCourse.variant);
    setMenuItem(completeCourse.menuItem);
    setExtras(completeCourse.extras);
    setPrice(completeCourse.price);

    props.addPriceToTotal(completeCourse.price);
  }, []);

  return (
    <Tooltip
      label={`${amount} ${menuItem.name} ${variant.name} ($${variant.price})
              ${extras.map((extra) => `+ ${extra.name} ($${extra.price})`)}
              ${special}
      `}
    >
      <Box rounded="l" padding={2} shadow="md">
        <CloseButton
          onClick={() => props.remove(props.course)}
          display="inline"
        />
        <Text fontSize={20}>
          {amount} {menuItem.name}
        </Text>
        <Box spacing="2">
          <Text display="inline" fontSize={17} color="gray">
            {variant.name}
          </Text>
          {special && <Tag> {special}</Tag>}

          {extras.map((extra) => (
            <Tag>{extra.name}</Tag>
          ))}

          {aditionalOptionsConfiguration.map((config) => (
            <Tag>
              <Text>
                <strong fontWeight={"bold"}>{config.propertyName}: </strong>
                {" " + config.value}
              </Text>
            </Tag>
          ))}
        </Box>

        <Table size='sm'>
          <Tbody>
            <Tr>
              <Td>inches</Td>
              <Td>millimetres (mm)</Td>
              <Td isNumeric>25.4</Td>
            </Tr>
            <Tr>
              <Td>feet</Td>
              <Td>centimetres (cm)</Td>
              <Td isNumeric>30.48</Td>
            </Tr>
            <Tr>
              <Td>yards</Td>
              <Td>metres (m)</Td>
              <Td isNumeric>0.91444</Td>
            </Tr>
          </Tbody>
          <Tfoot>
            <Tr>
              <Th>To convert</Th>
              <Th>into</Th>
              <Th isNumeric>multiply by</Th>
            </Tr>
          </Tfoot>
        </Table>

        <CoursesGroup plate={menuItem} amount={amount} display="inline" />

        <Stat>
          <StatNumber>${price}</StatNumber>
          <StatHelpText></StatHelpText>
        </Stat>
      </Box>
    </Tooltip>
  );
};

export const ShoppingCart = (props) => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const [total, setTotal] = useState(0);

  const addPriceToTotal = (coursePrice) =>
    setTotal((total) => total + coursePrice);

  return (
    <>
      <Button key="lg" m={4} onClick={onOpen} colorScheme="green">
        Ver Pedido ({props.shoppingCart.length})
      </Button>

      {isOpen && (
        <Modal onClose={onClose} size={"lg"} isOpen={isOpen}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Pedido</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Box as={motion.div}>
                {props.shoppingCart.map((course) => (
                  <ShoppingCartLine
                    course={course}
                    remove={props.remove}
                    addPriceToTotal={addPriceToTotal}
                  />
                ))}

                <SimpleGrid
                  columns={[1, 2]}
                  position="sticky"
                  bottom="1vh"
                  rounded="lg"
                  m="5"
                  spacing={2}
                >
                  <Button onClick={onClose}>Añadir más productos</Button>
                  <Button colorScheme="green">
                    Pedir (${props.shoppingCart != [] ? total : "0"}){" "}
                  </Button>
                </SimpleGrid>
              </Box>
            </ModalBody>
            <ModalFooter></ModalFooter>
          </ModalContent>
        </Modal>
      )}
    </>
  );
};
