import React, { useEffect, useState } from "react"

export const Menu = () => {
	
	const [menuItems, setMenuItems] = useState([])

	useEffect(() =>{

		fetch("http://192.168.1.60:5000/api/plate/all")
		.then(res => res.json())
		.then( res => setMenuItems(res))

	}, [])


	if(menuItems){
		return menuItems.map(item => (
			<div>
				<h3>{"item.name"}</h3>
				<p>{"item.description"}</p>
				<h4>${"item.price"}</h4>
				<img src={"item.imgURL"} />
			</div>
		))
	}else {
		return "loading"
	}

}
