import { useEffect } from "react";
import { useState } from "react/cjs/react.development";

export const Command = (props) => {
  const { kitchen, date, time, waiter, table, _id } = props.command;

  const [courses, setCourses] = useState([])

  // const starterCourses = courses.filter(course => course.isStarter)

  // const mainCourses = courses.filter(course => !course.isStarter)

  useEffect(() => {
    fetch(`http://localhost:5000/api/course/command/${_id}`)
    .then(res => res.json())
    .then(res => setCourses(res))
  }, []);

  const mainCourses = courses.filter(course => !course.isStarter)
  const starterCourses = courses.filter(course => course.isStarter)

  const Course = props => {

    const {plate_id, extra} = props.course
    
    return(
      <div style={{border: 'solid black 1px'}}>
      <h4>{plate_id}</h4>
        {extra}
    </div>
  )
}


  return (
    <div style={{ border: "solid black 1px" }}>
      <ul>
        <li>Cocina: {kitchen}</li>
        <li>Fecha: {date}</li>
        <li>Hora: {time}</li>
        <li>Mozo: {waiter}</li>
        <li>Mesa: {table}</li>
        <li>
          Entradas:
        <ul>
        {starterCourses.map(course =><li>
           <Course course={course}/>
        </li>)}
        </ul>
        </li>

        <li>
          Principales:
        <ul>
        {mainCourses.map(course =><li>
          <Course course={course}/>

        </li>)}
        </ul>
        </li>

      </ul>
    </div>
  );
};
