import { useState } from "react";
import { MenuItemIcon } from "../menu/menuItemIcon";
import { EditMenuItemWindow } from "./editMenuItemWindow";

export const MenuItemNew = () => {
  const [isOpen, setIsOpen] = useState(false);

  const close = () => setIsOpen(false);
  const open = () => setIsOpen(true);

  const emptyMenuItem = {
    name: "",
    category: "",
    description: "",
    imgURL: "",
    variants: [],
    tags: [],
    extras: [],
    aditionalOptions: [],
  };

  return (
    <>
      <Button>Nuevo Plato</Button>
      <MenuItemIcon item={emptyMenuItem} open={open} />
      {isOpen && (
        <EditMenuItemWindow
          item={emptyMenuItem}
          close={close}
        />
      )}
    </>
  );
};
