import {
  Alert,
  AlertDescription,
  AlertIcon,
  AlertTitle,
  Box,
  CircularProgress,
  CircularProgressLabel,
  Heading,
  Progress,
  Text,
} from "@chakra-ui/react";
import { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { server } from "../../../config";

const Course = ({ course }) => {
  const {
    amount,
    special,
    courseMoment,
    aditionalOptionsConfiguration,
    variant,
    menuItem,
    extras,
    price,
  } = course;
  return (
    <Box>
      <Text>
        {amount} x {menuItem.name} : {variant.name}{" "}
      </Text>
      <Box ml={10}>
        <Text>{special}</Text>
        <Text>
          {aditionalOptionsConfiguration.map((option) => (
            <Text>
              {" "}
              {option.propertyName}: {option.value}{" "}
            </Text>
          ))}
        </Text>

        <Text>
          {extras.map((extra) => (
            <Text>+{extra.name}</Text>
          ))}
        </Text>
      </Box>
    </Box>
  );
};

export const Order = ({ order }) => {
  const [courses, setCourses] = useState([]);
  const [end, setEnd] = useState(0);
  const { _id, timeDate, deliveryLocation, status, orderNote } = order;

  let date = new Date(null);
  date.setMilliseconds(end - Date.parse(timeDate)); // specify value for SECONDS here
  var delayClock = date.toISOString().substring(14, 19);

  const delayTreshold = 300;
  const delaySeconds = (end - Date.parse(timeDate)) / 1000;
  const percentageToDelayed = (delaySeconds / delayTreshold) * 100;
  const isDelayed = delaySeconds > delayTreshold;

  useEffect(() => {
    setInterval(() => {
      setEnd(Date.now());
    }, 1000);
  }, []);

  useEffect(() => {
    fetch(server + "/api/order/courses/" + _id)
      .then((res) => res.json())
      .then((res) => setCourses(res));
  }, []);

  return (
    <Box p={5} m={3} shadow="lg" rounded="lg" bg={isDelayed ? "red": "green"}> 
      <CircularProgress value={percentageToDelayed} color="red.400" mt={-16} >
        <CircularProgressLabel >{delayClock}</CircularProgressLabel>
      </CircularProgress>
      <Heading size={"lg"}>{deliveryLocation}</Heading>

      <Alert status={isDelayed ? "error" : "info"}>
        <AlertIcon />
        <AlertTitle mr={2}>
          {isDelayed ? "Pedido Atrasado!" : "Nuevo Pedido"}
        </AlertTitle>

        <AlertDescription></AlertDescription>
      </Alert>

      {orderNote != "unknown extras" && <Text>{orderNote}</Text>}

      {courses.map((course) => (
        <Course course={course} />
      ))}
    </Box>
  );
};
