import {
  Badge,
  Box,
  Button,
  Heading,
  HStack,
  Image,
  Input,
  NumberDecrementStepper,
  NumberIncrementStepper,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  SimpleGrid,
  Text,
  Wrap,
  WrapItem,
} from "@chakra-ui/react";
import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { CloseButton } from "react-bootstrap";
import { PlateIcon } from "./plateIcon";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const [selectedItem, setSelectedItem] = useState(null);

  const addToShoppingCart = () => {
    setShoppingCart(
      shoppingCart.concat({ ...selectedItem, amount: 1, extra: null })
    );
    setSelectedItem(null);
  };
  return (
    <>
      <SimpleGrid columns={[1, 2, 3]} spacing="40px">
        {menuItems.map((item) => (
          <PlateIcon item={item} setSelectedItem={setSelectedItem} />
        ))}
      </SimpleGrid>
      <AnimatePresence>
        {selectedItem && (
          <ItemWindow />
        )}
      </AnimatePresence>
      {shoppingCart != [] && (
        <Box
          as={motion.div}
          position="fixed"
          bottom="0"
          right="0"
          boxShadow="lg"
          padding="3"
          bg="white"
          animate={{ opacity: 1 }}
          initial={{ opacity: 0 }}
        >
          <Heading>shopping cart</Heading>

          {shoppingCart.map((item) => (
            <HStack>
              <Box as={motion.div}>
                <Image as={motion.img} src={item.imgURL} w="70" h="70" />
              </Box>
              <Box as={motion.div}>
                {item.amount} X {item.name}
              </Box>
            </HStack>
          ))}
        </Box>
      )}
    </>
  );
};
