import { AnimatePresence, motion } from "framer-motion";
import React, { useEffect, useState } from "react";
import { Badge, Button, Card, Col, Container, Modal, Row } from "react-bootstrap";

export const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.60:5000/api/plate/all")
      .then((res) => res.json())
      .then((res) => setMenuItems(res));
  }, []);

  const categoryNames = [...new Set(menuItems.map((item) => item.category))];

  const categories = categoryNames
    .map((name) => {
      return {
        name: name,
        items: menuItems.filter((item) => item.category == name),
      };
    })
    .reverse();

  const [selectedItem, setSelectedItem] = useState(null);

  return (
    <>
      <Container className="col-12">
        {menuItems.map((item) => (
          <motion.div
            as={motion.div}
            style={{
              height: "22rem",
              float: "left",
              margin: 20,
              backgroundSize: "100%",
            }}
            layoutId={item._id}
            onClick={() => setSelectedItem(item)}
            className="col-4"
            rounded
          >
            <Card.Img
              as={motion.img}
              src={item.imgURL}
              variant="top"
              className="rounded-lg "
              style={{
                objectFit: "cover",
                height: "70%",
                borderRadius: 15,
              }}
            />
            <motion.div>
              <motion.h4 style={{ textAlign: "center", margin: 10 }}>
                {item.name} <Badge pill>${item.price}</Badge>
              </motion.h4>
            </motion.div>
          </motion.div>
        ))}
      </Container>
      <AnimatePresence>
        {selectedItem && (
          <>
            <motion.div
              style={{
                width: "100%",
                height: "100%",
                backgroundColor: "white",
                position: "fixed",
                top: 0,
              }}
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.5 }}
              onClick={() => setSelectedItem(null)}
            />
            <motion.div
              style={{
                position: "fixed",
                top: "0%",
                left: "0%",
                marginLeft: "20%",
                marginTop: "10%",
                padding: 20,
                background: "white",
                width: "60%",
                height: "60%",
                borderRadius: 20,
              }}
              layoutId={selectedItem._id}
              onClick={() => setSelectedItem(selectedItem)}
              className="shadow-lg "
            >
              <Row as={motion.div} >
                <Col>
                  <motion.img
                    variant="top"
                    src={selectedItem.imgURL}
                    style={{ borderRadius: 20 }}
                  />
                </Col>
                <Col as={motion.div} className="m-3">
                    <h2>{selectedItem.name}</h2>
                    <p>{selectedItem.description}</p>
                    <Button variant="primary">${selectedItem.price}</Button>
                </Col>
              </Row>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </>
  );

  // return categories.map((cat) => (
  //   <>
  //     <h1>{cat.name}</h1>
  //     {cat.items.map((item) => (
  //       <Plate plate={item} />
  //     ))}
  //   </>
  // ));
};
