import { Box, CircularProgress, CircularProgressLabel, Text } from "@chakra-ui/react";
import { useEffect } from "react";
import { useState } from "react/cjs/react.development";
import { server } from "../../../config";

const Course = ({ course }) => {
  const {
    amount,
    special,
    courseMoment,
    aditionalOptionsConfiguration,
    variant,
    menuItem,
    extras,
    price,
  } = course;
  return (
    <Box p={5} border={"1px solid gray"}>
      <Text>
        {amount} x {menuItem.name} : {variant.name}{" "}
      </Text>

      <Text>{special}</Text>
      <Text>
        {aditionalOptionsConfiguration.map((option) => (
          <Text>
            {" "}
            {option.propertyName}: {option.value}{" "}
          </Text>
        ))}
      </Text>

      <Text>
        {extras.map((extra) => (
          <Text>+{extra.name}</Text>
        ))}
      </Text>
    </Box>
  );
};

export const Order = ({ order }) => {
  const [courses, setCourses] = useState([]);
  const [end, setEnd] = useState(0);
  const { _id, timeDate, deliveryLocation, status, orderNote } = order;

  let date = new Date(null);
  date.setMilliseconds(end - Date.parse(timeDate)); // specify value for SECONDS here
  var result = date.toISOString().substring(14, 19);

  useEffect(() => {
    fetch(server + "/api/order/courses/" + _id)
      .then((res) => res.json())
      .then((res) => setCourses(res));

    setInterval(() => {
      setEnd(Date.now());
    }, 1000);
  }, []);

  return (
    <Box p={5} border={"1px solid gray"}>
      <CircularProgress value={20} color="green.400">
        <CircularProgressLabel>{result}</CircularProgressLabel>
      </CircularProgress>
      <Text>Mesa {deliveryLocation}</Text>
      <Text>{status}</Text>
      {orderNote != "unknown extras" && <Text>{orderNote}</Text>}

      {courses.map((course) => (
        <Course course={course} />
      ))}
    </Box>
  );
};
