export const PlateIcon = () => {
	return <Box
	  as={motion.div}
	  style={{
	    height: "400px",
	    backgroundSize: "100%",
	  }}
	  onClick={() => setSelectedItem(item)}
	  layoutId={item.token}
	  animate={{ scale: 1 }}
	  initial={{ scale: 0.5 }}
	>
	  <Box as={motion.div} layoutId={item._id} style={{ height: "70%" }}>
	    <Image
	      as={motion.img}
	      src={item.imgURL}
	      variant="top"
	      className="rounded-lg "
	      style={{
		objectFit: "cover",
		height: "100%",
		width: "100%",
		borderRadius: 15,
	      }} />
	  </Box>
	  <Box as={motion.div} layoutId={item.name}>
	    <Heading size="md" m="2">
	      {item.name} <Badge colorScheme="green">${item.price}</Badge>
	    </Heading>
	  </Box>
	</Box>;
      }
